package org.ton.tonkotlinusecase.contracts.nft

import org.ton.block.AddrStd
import org.ton.boc.BagOfCells
import org.ton.cell.Cell
import org.ton.crypto.hex
import org.ton.lite.client.LiteClient

/**
 * @explicitAddress - if you need to use external NFT and you don't know source code
 *                      of those contract, but you already know the address
 */
class NFTV1(
    liteClient: LiteClient,
    ownerAddress: AddrStd,
    metadataURL: String,
    explicitAddress: AddrStd? = null
) : AbstractNftContract(liteClient, ownerAddress, metadataURL) {
    override val name: String = "Single NFT"

    override val code: Cell = CODE

    override val address = explicitAddress ?: this.address()

    // https://github.com/ton-blockchain/token-contract/blob/7d20957ea8d73a6af6e37a7b206db982faa346a3/nft/nft-item.fc
    companion object {
        val CODE: Cell =
            BagOfCells(hex("")).first()
    }
}
