package org.ton.tonkotlinusecase.contracts.wallet

import org.ton.api.pk.PrivateKeyEd25519
import org.ton.block.AddrStd
import org.ton.block.Coins
import org.ton.block.StateInit
import org.ton.boc.BagOfCells
import org.ton.cell.Cell
import org.ton.cell.CellBuilder
import org.ton.crypto.hex
import org.ton.lite.api.LiteApi
import org.ton.lite.api.liteserver.LiteServerSendMsgStatus
import org.ton.tonkotlinusecase.constants.SendMode

class WalletV4R2(
    liteApi: LiteApi,
    privateKey: PrivateKeyEd25519
) : AbstractWallet(liteApi, privateKey) {

    override val name: String = "v4r2"
    override val code: Cell = CODE

    companion object {
        // https://github.com/toncenter/tonweb/blob/master/src/contract/wallet/WalletSources.md#revision-2-3
        val CODE: Cell =
            BagOfCells(
                hex("")
            ).first()
    }

    suspend fun sendInternal(
        dest: AddrStd,
        value: Long,
        bounce: Boolean? = null,
        body: Cell? = null,
        destinationStateInit: StateInit? = null,
    ): LiteServerSendMsgStatus {

        return liteApi.sendMessage(
            createTransferMessageV2(
                dest = dest,
                bounce = bounce ?: true,
                amount = Coins.ofNano(value),
                seqno = seqno(),
                payload = body ?: CellBuilder.createCell {},
                sendMode = SendMode.PAY_GAS_SEPARATELY,
                destinationStateInit = destinationStateInit
            )
        )
    }
}
